INSERT INTO articles(title, announce, full_text, picture, user_id) VALUES
('Что такое золотое сечение', 'Программировать не настолько сложно, как об этом говорят.', 'Рок-музыка всегда ассоциировалась с протестами. Так ли это на самом деле? Игры и программирование разные вещи. Не стоит идти в программисты, если вам нравятся только игры. Простые ежедневные упражнения помогут достичь успеха. Освоить вёрстку несложно. Возьмите книгу новую книгу и закрепите все упражнения на практике.', 'item07.jpg', '3'),
